  <div class="card-body">
    <WPlaceholder tag="h5" class="card-title" glow>
      <Placeholder :col="6"></Placeholder>
    </WPlaceholder>
    <WPlaceholder tag="p" class="card-text" glow>
      <WPlaceholder :col="7"/>
      <WPlaceholder :col="4"/>
      <WPlaceholder :col="4"/>
      <WPlaceholder :col="6"/>
      <WPlaceholder :col="8"/>
    </WPlaceholder>
    <a href="#" tabindex="-1" class="btn btn-primary disabled placeholder col-6"></a>
  </div>
</div>
